import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RouterForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    login: '',
    password: '',
  });
  const [selectedOptions, setSelectedOptions] = useState({
    name: false,
    interfaces: false,
    load: false,
    encryption: false,
    blockedResources: false,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleToggleChange = (name) => {
    setSelectedOptions({ ...selectedOptions, [name]: !selectedOptions[name] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/generate-report', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: formData.login,
          options: selectedOptions,
        }),
      });
      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        navigate('/report-name', { state: { pdfUrl: url, login: formData.login } });
      } else {
        alert('Ошибка генерации отчёта!');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="form-container">
      <h1>Настройка отчёта для роутера</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Логин:</label>
          <input
            type="text"
            name="login"
            value={formData.login}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Пароль:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="options-group">
          <h3>Выберите данные для отчёта:</h3>
          {Object.entries(selectedOptions).map(([key, value]) => (
            <div key={key} className="toggle-switch">
              <label>
                {key === 'name' && 'Название роутера'}
                {key === 'interfaces' && 'Сетевые интерфейсы'}
                {key === 'load' && 'Загруженность CPU/RAM'}
                {key === 'encryption' && 'Настройки шифрования'}
                {key === 'blockedResources' && 'Заблокированные ресурсы'}
                <input
                  type="checkbox"
                  checked={value}
                  onChange={() => handleToggleChange(key)}
                  className="toggle-input"
                />
                <span className="toggle-slider"></span>
              </label>
            </div>
          ))}
        </div>

        <button type="submit" className="submit-btn">
          Сформировать отчёт
        </button>
      </form>
    </div>
  );
};

export default RouterForm;
