import React, { useState, useEffect } from 'react';

const ReportName = () => {
  const [reportName, setReportName] = useState('');
  const currentDate = new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19);

  useEffect(() => {
    // Автоматическое название по умолчанию (username_data_time)
    const defaultName = `router_report_${currentDate}`;
    setReportName(defaultName);
  }, []);

  const handleNameChange = (e) => {
    setReportName(e.target.value);
  };

  const handleSave = () => {
    // В будущем здесь будет генерация PDF
    alert(`Отчёт "${reportName}" будет сохранён в PDF`);
  };

  return (
    <div className="report-name-container">
      <h1>Название отчёта</h1>
      <div className="input-group">
        <label>Имя файла:</label>
        <input
          type="text"
          value={reportName}
          onChange={handleNameChange}
          placeholder="Введите название отчёта"
        />
      </div>
      <button onClick={handleSave} className="save-btn">
        Сохранить отчёт
      </button>
    </div>
  );
};

export default ReportName;
