import React from 'react';
import { useLocation } from 'react-router-dom';

const ReportPage = () => {
    const { state } = useLocation();
    
    // Если state не определен (прямой переход на /report), перенаправляем на главную
    if (!state) {
        window.location.href = '/';
        return null;
    }

    const { login, report = [], status, message } = state;

    if (status === 'error') {
        return (
            <div className="report-container">
                <h1>Ошибка</h1>
                <p>{message}</p>
            </div>
        );
    }

    return (
        <div className="report-container">
            <h1>Отчёт для пользователя: {login}</h1>
            <div className="report-content">
                {report.length > 0 ? (
                    report.map((item) => (
                        <div key={item.key} className="report-section">
                            <h2>{item.title}</h2>
                            <p>{item.content}</p>
                        </div>
                    ))
                ) : (
                    <p>Нет данных для отображения. Выберите хотя бы один раздел отчета.</p>
                )}
            </div>
        </div>
    );
};

export default ReportPage;
