import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const ReportName = () => {
  const { state } = useLocation();
  const [reportName, setReportName] = useState('');

  useEffect(() => {
    if (state?.login) {
      const now = new Date();
      const localTime = now.toLocaleString('ru-RU', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false,
      }).replace(/[\/,:]/g, '-').replace(/\s+/g, '_');
      
      const defaultName = `${state.login}_${localTime}`;
      setReportName(defaultName);
    }
  }, [state]);

  const handleNameChange = (e) => {
    setReportName(e.target.value);
  };

  const handleSave = () => {
    alert(`Отчёт "${reportName}" будет сохранён в PDF`);
  };

  return (
    <div className="report-name-container">
      <h1>Название отчёта</h1>
      <div className="input-group">
        <label>Имя файла:</label>
        <input
          type="text"
          value={reportName}
          onChange={handleNameChange}
          placeholder="Введите название отчёта"
        />
      </div>
      <button onClick={handleSave} className="save-btn">
        Сохранить отчёт
      </button>
    </div>
  );
};

export default ReportName;
