import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RouterForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    login: '',
    password: '',
  });
  const [selectedOptions, setSelectedOptions] = useState({
    name: false,
    interfaces: false,
    load: false,
    encryption: false,
    blockedResources: false,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setSelectedOptions({ ...selectedOptions, [name]: checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // В будущем здесь будет логика отправки данных
    navigate('/report-name');
  };

  return (
    <div className="form-container">
      <h1>Настройка отчёта для роутера</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Логин:</label>
          <input
            type="text"
            name="login"
            value={formData.login}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Пароль:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="options-group">
          <h3>Выберите данные для отчёта:</h3>
          <label>
            <input
              type="checkbox"
              name="name"
              checked={selectedOptions.name}
              onChange={handleCheckboxChange}
            />
            Название роутера
          </label>
          <label>
            <input
              type="checkbox"
              name="interfaces"
              checked={selectedOptions.interfaces}
              onChange={handleCheckboxChange}
            />
            Сетевые интерфейсы
          </label>
          <label>
            <input
              type="checkbox"
              name="load"
              checked={selectedOptions.load}
              onChange={handleCheckboxChange}
            />
            Загруженность CPU/RAM
          </label>
          <label>
            <input
              type="checkbox"
              name="encryption"
              checked={selectedOptions.encryption}
              onChange={handleCheckboxChange}
            />
            Настройки шифрования
          </label>
          <label>
            <input
              type="checkbox"
              name="blockedResources"
              checked={selectedOptions.blockedResources}
              onChange={handleCheckboxChange}
            />
            Заблокированные ресурсы
          </label>
        </div>

        <button type="submit" className="submit-btn">
          Сформировать отчёт
        </button>
      </form>
    </div>
  );
};

export default RouterForm;
